                    UserId = 1,
                    UserName = "Admin",
                    Roles = new Collection<Role> { Roles.SingleOrDefault(r => r.RoleName == "Administrator") }
                };
            Users.Add(admin);
            SaveChanges();
            Memberships.Add(new Membership
                {
                    UserId = 1,
                    CreateDate = DateTime.Now,
                    IsConfirmed = true,
                    Password = Crypto.HashPassword("administrator"),
